<?php/** * The template for displaying all pages. * * @package Risana */ get_header(); ?> <?php while (have_posts()) : the_post(); ?>		<div class="blue-header">			<div class="img-container news-header-bg" <?php if(get_theme_mod('bg_image_blog')) { ?> style="background-image: url('<?php echo esc_url(get_theme_mod('bg_image_blog')); ?>')"  <?php }  ?>></div>			<div class="container">				<h3 class="white with-shadow"><?php echo esc_html(get_theme_mod('title_blog_page')); ?></h3>				<p class="white with-shadow"><?php echo esc_html(get_theme_mod('sub_title_blog_page')); ?></p>			</div>		</div>        <div class="container">			<div class="column-container">				<div class="column-9-12">					<div class="news-container news-single">						<div class="gutter">							<div class="news-item">								<h3 class="blue-border"><?php the_title(); ?></h3>								<?php if ( has_post_thumbnail() && ! post_password_required() ) : ?>								<div class="img-container">									<a href="<?php the_permalink() ?>" ><?php the_post_thumbnail('featured'); ?></a>								</div>														<?php endif; ?>									<div class="meta-container">									<div class="meta">										<p class="user"><i class="fa fa-user"></i><?php the_author(); ?></p>										<p class="category"><i class="fa fa-folder"></i><?php the_category(', '); ?></p>										<p class="comments"><i class="fa fa-comments"></i><?php comments_popup_link( 'No comments', '1 comment', '% comments', 'comments-link', 'Comments are off'); ?></p>										<div class="date-container"><span><?php the_time( get_option( 'date_format' ) ); ?></span>										</div>									</div>								</div>								<div class="text">									<div class="editcontent"><?php the_content(); ?></div>								</div>							</div>							<p class="the-blog-item-general-info"><?php the_tags(); ?></p>						    <p><?php posts_nav_link(); ?></p>						    <div class="padinate-page"><?php wp_link_pages(); ?></div>								<div class="comments-container">								<?php comments_template(); ?>							</div>													</div>					</div>				</div>				<div class="column-3-12">					 <?php  get_sidebar(); ?>				</div>			</div>		</div><?php endwhile; ?><?php get_footer(); ?>